completion:
class Solution:
    def dividePlayers(self, skill: List[int]) -> int:
        skill.sort()
        if len(skill) % 2 != 0:
            return -1
        else:
            n = len(skill)
            left = skill[:n//2]
            right = skill[n//2:]
            left.reverse()
            res = 0
            for i in range(n//2):
                res += left[i] * right[i]
            return res

