class Solution:
    def pivotInteger(self, n: int) -> int:
        if n == 1:
            return 1
        left = 1
        right = n
        while left <= right:
            mid = (left + right) // 2
            left_sum = (mid * (mid + 1)) // 2
            right_sum = (n * (n + 1)) // 2 - ((mid + 1) * (mid + 2)) // 2
            if left_sum == right_sum:
                return mid
            elif left_sum > right_sum:
                right = mid - 1
            else:
                left = mid + 1
        return -1

