class Solution:
    def minimumFuelCost(self, roads: List[List[int]], seats: int) -> int:
        # BFS
        # build graph
        graph = {}
        for i, j in roads:
            if i not in graph:
                graph[i] = []
            if j not in graph:
                graph[j] = []
            graph[i].append(j)
            graph[j].append(i)

        # BFS
        visited = set()
        queue = deque([0])
        cost = 0
        while queue:
            for _ in range(len(queue)):
                node = queue.popleft()
                if node not in visited:
                    cost += 1
                    for n in graph[node]:
                        queue.append(n)
                    visited.add(node)
            if len(visited) >= seats:
                return cost
        return cost
