# Definition for a binary tree node.
# class TreeNode:
#     def __init__(self, val=0, left=None, right=None):
#         self.val = val
#         self.left = left
#         self.right = right
class Solution:
    def closestNodes(self, root: Optional[TreeNode], queries: List[int]) -> List[List[int]]:
        def inorder(root):
            if root:
                yield from inorder(root.left)
                yield root.val
                yield from inorder(root.right)

        def closest(root, target):
            closest = [float('inf'), float('inf')]
            for val in inorder(root):
                if abs(val - target) < abs(closest[0] - target):
                    closest = [val, closest[0]]
                elif abs(val - target) == abs(closest[0] - target):
                    closest.append(val)
            return closest

        return [closest(root, target) for target in queries]

