class Solution:
    def mostProfitablePath(self, edges: List[List[int]], bob: int, amount: List[int]) -> int:
        # build graph
        graph = {}
        for u, v in edges:
            graph[u] = graph.get(u, []) + [v]
            graph[v] = graph.get(v, []) + [u]

        # initialize dp
        dp = [0] * len(amount)

        # dfs
        def dfs(node, parent):
            # base case
            if node == bob:
                dp[node] = amount[node]
                return

            # dfs
            for child in graph[node]:
                if child != parent:
                    dfs(child, node)

            # update dp
            dp[node] = max(amount[node], sum(dp[child] for child in graph[node] if child != parent))

        # run dfs
        dfs(0, -1)

        # return result
        return dp[0]

