                if robot[i] >= factory[j][0]:
                    result += factory[j][0] - robot[i]
                    i += 1
                    j += 1
                else:
                    result += factory[j][0] - robot[i]
                    i += 1
            else:
                result += robot[i] - factory[j-1][0]
                i += 1
        return result

