class Solution:
    def secondGreaterElement(self, nums: List[int]) -> List[int]:
        # create a dictionary to store the index of the first greater element
        d = {}
        for i in range(len(nums)):
            d[nums[i]] = i

        # create a list to store the result
        result = []
        for i in range(len(nums)):
            # if the current element is the largest in the list, append -1
            if nums[i] == max(nums):
                result.append(-1)
            # if the current element is not the largest in the list, append the second largest
            else:
                # create a list to store the elements greater than the current element
                greater_elements = []
                for j in range(i+1, len(nums)):
                    if nums[j] > nums[i]:
                        greater_elements.append(nums[j])
                # sort the list of greater elements and append the second largest
                greater_elements.sort()
                result.append(greater_elements[0])
        return result
