completion:
class Solution:
    def makeSimilar(self, nums: List[int], target: List[int]) -> int:
        # Create a dictionary of the target list
        target_dict = {}
        for i in target:
            if i not in target_dict:
                target_dict[i] = 1
            else:
                target_dict[i] += 1

        # Create a dictionary of the nums list
        nums_dict = {}
        for i in nums:
            if i not in nums_dict:
                nums_dict[i] = 1
            else:
                nums_dict[i] += 1

        # Create a counter for the number of moves
        moves = 0

        # Loop through the nums dictionary
        for i in nums_dict:
            # If the value of the target dictionary is less than the value of the nums dictionary
            if target_dict[i] < nums_dict[i]:
                # Add the difference to the moves counter
                moves += nums_dict[i] - target_dict[i]

        # Return the moves counter
        return moves

