class Solution:
    def hardestWorker(self, n: int, logs: List[List[int]]) -> int:
        # Time Complexity: O(n)
        # Space Complexity: O(n)
        # n = number of employees
        # m = number of tasks
        # logs = [id, time]
        # A: init a dict to track the total time for each employee
        employee_times = {i:0 for i in range(n)}
        # B: iterate over the logs, update the total time for each employee
        for log in logs:
            employee_times[log[0]] += log[1] - log[0]
        # C: return the employee with the max total time
        return max(employee_times, key=employee_times.get)

